// Get the input data from previous node
const inputData = $input.first().json;
const xmlString = inputData.schema;

console.log(inputData)

// Function to extract content between XML tags with better regex handling
function extractFromXmlTags(xmlString, tagName) {
  const regex = new RegExp(`<${tagName}>(.*?)<\/${tagName}>`, 'gs');
  const match = regex.exec(xmlString);
  return match ? match[1].trim() : null;
}

// Get the platform from the input or use a default
const platform = inputData.platform;

// Extract the content from the specified tag
const extractedContent = extractFromXmlTags(xmlString, platform);
const rootContent = extractFromXmlTags(xmlString, 'root');
const commonContent = extractFromXmlTags(xmlString, 'common');

jsonData = JSON.parse(extractedContent);
rootSchema = JSON.parse(rootContent);
commonSchema = JSON.parse(commonContent);

// Return the result
return {
  json: {
    schema: jsonData,
    root_schema: rootSchema,
    common_schema: commonSchema
  }
};

// Get the input data from previous node
const inputData = $input.first().json;
const xmlString = inputData.system_prompt;

// Function to extract all content between XML tags
function extractAllXmlTags(xmlString) {
  // Create a result object to store tag contents
  const result = {};

  // Regular expression to find all XML tags and their content
  // This regex matches opening tag, content, and closing tag
  const tagRegex = /<([^>\/]+)>([\s\S]*?)<\/\1>/g;

  // Find all matches
  let match;
  while ((match = tagRegex.exec(xmlString)) !== null) {
    const tagName = match[1].trim();
    const content = match[2].trim();

    // Store the content with the tag name as the key
    result[tagName] = content;
  }

  return result;
}

// Extract all XML tags and their content
const extractedTags = extractAllXmlTags(xmlString);

// Return the result as a JSON object
return {
  json: {
    system_config: extractedTags
  }
};

