const currentData = $('Summarize Meta input (current year)').first().json;
const previousData = $('Summarize Meta data input (previous year)').first().json;

function parseNumber(value) {
    if (typeof value === 'string') {
        return parseFloat(value.replace(/\./g, '').replace(',', '.'));
    }
    return value || 0;
}

function formatNumber(number, decimals = 0) {
    if (number === 0) return "0";
    return parseNumber(number).toLocaleString('de-DE', {
        minimumFractionDigits: decimals,
        maximumFractionDigits: decimals
    });
}

function calculateCPM(cpm) {
    return parseNumber(cpm);
}

return [{
    current_impressions: formatNumber(currentData.sum_Impressions),
