const currentData = $('Summarize input (current year)').first().json;
const previousData = $('Summarize input (previous year)').first().json;

function formatNumber(number, decimals = 0) {
   if (number === 0) return "0";
   return number.toLocaleString('de-DE', {
       minimumFractionDigits: decimals,
       maximumFractionDigits: decimals
   });
}

function calculateCPM(costMicros, impressions) {
   if (impressions === 0) return 0;
   return (costMicros / 1000000 / impressions) * 1000;
}

return [{
   current_impressions: formatNumber(currentData.sum_Impressions),
   current_cpm: formatNumber(calculateCPM(currentData.sum_Spend, currentData.sum_Impressions), 2),
   current_clicks: formatNumber(currentData.sum_Clicks),
   current_ctr: formatNumber(currentData.average_CTR * 100, 2),
   current_conversions: formatNumber(currentData.sum_Conversions, 2),
   current_cost_per_conversion: formatNumber(currentData.average_Cost_per_Conversion / 1000000, 2),
   current_cost: formatNumber(currentData.sum_Spend / 1000000, 2),
   current_roas: formatNumber(currentData.average_ROAS, 2),

   previous_impressions: formatNumber(previousData.sum_Impressions),
   previous_cpm: formatNumber(calculateCPM(previousData.sum_Spend, previousData.sum_Impressions), 2),
   previous_clicks: formatNumber(previousData.sum_Clicks),
   previous_ctr: formatNumber(previousData.average_CTR * 100, 2),
   previous_conversions: formatNumber(previousData.sum_Conversions, 2),
   previous_cost_per_conversion: formatNumber(previousData.average_Cost_per_conversion / 1000000, 2),
   previous_cost: formatNumber(previousData.sum_Spend / 1000000, 2),
   previous_roas: formatNumber(previousData.average_ROAS, 2)
}];

