let outline = $input.first().json.output[0].outline || '';

// Step 1: Remove ```markdown and ``` fences
outline = outline
  .replace(/```markdown\n?/g, '')   // remove opening ```markdown
  .replace(/```/g, '')              // remove closing ```
  .trim();

// Step 2: Escape backslashes and double quotes
outline = outline
  .replace(/\\/g, '\\\\')           // escape backslashes
  .replace(/"/g, '\\"');            // escape double quotes

return {
    _id : $('Code7').first().json._id,
    blog_outline: outline,
    completed_step : 5
  }

function escapeQuotes(str) {
  if (typeof str !== 'string') return str;
  return str.replace(/"/g, "'");
}

const articles = $input.first().json.articles.map(article => {
  const sanitized = {};

  // Loop through keys and escape problematic strings
  for (const [key, value] of Object.entries(article)) {
    if (Array.isArray(value)) {
      sanitized[key] = value.map(v => escapeQuotes(v));
    } else if (typeof value === 'string') {
      sanitized[key] = escapeQuotes(value);
    } else {
      sanitized[key] = value;
    }
  }

  return sanitized;
});

const item_id = $('MongoDB1').first().json._id

return {
    _id: item_id,
    articles: articles,
    completed_step: 3
  }

const input = $('AI Agent3').first().json.output;

// Add average_score to each item
for (const item of input) {
  item.average_score = (
    (item.uniqueness_score || 0) +
    (item.seo_score || 0) +
    (item.longtail_score || 0)
  ) / 3;
}

// Sort by average_score descending
input.sort((a, b) => b.average_score - a.average_score);

// Return full sorted list + top blog title
return {
    blog_title_ideas: input,
    blog_title: input[0].title,
    core_topic: $('AI Agent2').first().json.output.core_topic , 
