const markdown = $('MongoDB8').first().json.blog_article || '';

// Simple replacements (limited Markdown support)
let html = markdown
  .replace(/^### (.*$)/gim, '<h3>$1</h3>')
  .replace(/^## (.*$)/gim, '<h2>$1</h2>')
  .replace(/^# (.*$)/gim, '<h1>$1</h1>')
  .replace(/\*\*(.*?)\*\*/gim, '<strong>$1</strong>')
  .replace(/\*(.*?)\*/gim, '<em>$1</em>')
  .replace(/\[(.*?)\]\((.*?)\)/gim, '<a href="$2">$1</a>')
  .replace(/\n$/gim, '<br />');

html = `<div>${html}</div>`; // wrap in container

return [{
  json: {
    blog_html: html
  }
}];

const base = $('MongoDB8').first().json.image_alt_text || '';
const cleanBase = base.replace(/["\n\r]/g, ''); // Remove double quotes and line breaks
const enrichedPrompt = `A cinematic, emotionally compelling scene involving people that captures the essence of: ${cleanBase}. Include visible human figures in natural interaction or meaningful posture, placed in a symbolic setting that reinforces the story.`;

return [{
  json: {
    prompt: enrichedPrompt
  }
}];

function stripHtmlTags(html) {
  return html
    .replace(/<\/?[^>]+(>|$)/g, '') // remove all HTML tags
    .replace(/&nbsp;/g, ' ')
    .replace(/&amp;/g, '&')
    .replace(/&quot;/g, '"')
    .replace(/&#39;/g, "'")
    .replace(/&lt;/g, '<')
    .replace(/&gt;/g, '>');
}

const input = $json.body || "";
const markdownContent = stripHtmlTags(input).trim();

return {
  json: {
    data: markdownContent
  }
};

// Get current item data
let article = $json.output || {};

article.link = $('Code10').first().json.link_articles;

// Return with flattened structure
return {
  json: {
    ...article
  }
};
