additionalFields.outputSchema:
{
  "type": "object",
  "properties": {
    "pricing_summary": {
      "type": "string",
      "description": "A textual description of the pricing, including the plan's name, price, and top 3 features."
    },
    "differences_summary": {
      "type": "string",
      "description": "A concise summary of the differences between the previous and current plans, focusing on changes."
    },
    "status": {
      "type": "string",
      "description": "Indicates if the new plan is substantially different from the previous one."
    }
  },
  "required": [
    "pricing_summary",
    "differences_summary",
    "status"
  ],
  "additionalProperties": false,
  "$schema": "http://json-schema.org/draft-07/schema#"
}

parameters.jsCode:
const response = JSON.parse($json.data.modelResponse)

return { json: {
  ...response,
  row_number: $json['row_number'],
  "Pricing URL": $json["Pricing URL"]
}}
